package io.openmaru.test.springboot3.test_springboot3.web;

import jakarta.servlet.http.HttpServletRequest;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;

import jakarta.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.io.PrintWriter;
import java.util.Enumeration;


@Controller
public class MainController {
    private static Logger logger = LoggerFactory.getLogger(MainController.class);

    @Autowired
    HttpServletRequest httpServletRequest;

    @GetMapping("/")
    public void index(HttpServletResponse res) throws IOException {
        logger.info("===== mai1");
        logger.info("");
        PrintWriter out = res.getWriter();
        out.println("index 1<br/>");
        out.println("<br/>Client IP: " + httpServletRequest.getRemoteAddr());
        out.println("<br/>Headers");
        Enumeration<String> headerNames = httpServletRequest.getHeaderNames();
        while (headerNames.hasMoreElements()) {
            String key = headerNames.nextElement();
            out.println("<br/>" + key + ": " + httpServletRequest.getHeader(key));
        }
        return;
    }
}